printf("다야몬드의 크기를 입력하시오.\n");
scanf("%d",&line);

for(i=1;i<=line;i++)
{
for(j=1;j<=line-i;j++)
{
printf(" ");
}
for(k=1;k<=2*i-1;k++)
{
printf("*");
}
printf("\n");
}
for(i=1;i<=line;i++)
{
for(j=0;j<=line-(line-(i-1));j++)
{
printf(" ");
}
for(k=1;k<=2*(line-i)-1;k++)
{
