if(fibo%2==0) //짝수이면 총합계산
{
n_sum = n_sum + n_1 + n_2;
}

n_2 = n_1; //n-2에 n-1값 대입
n_1 = i; //현재 실행값 입력
i = n_1 + n_2; //다음 실행값 입력

}
printf("%d\n", n_sum);
return 0;
}
