}

// 문제7 - 임의의 자연수 n을 입력받아 n을 소인수 분해 결과 출력하여라
// n=18 => 18=2*3*3
#include <stdio.h>
int main()
{
int n,m,i;  // n=자연수, m=인수, i=제어변수
printf("소인수 분해를 할 자연수 n을 입력하세요 => ");
scanf("%d",&n);
m=n;
printf("\n %d = ",n);
if(m==1)
{
printf(" %d\n",m);
return 0;
}
for(i=2;i<=m;i++)
{
for(;;)
{
if(m%i==0)
{
printf("%d",i);
if(i<m)
{
printf(" * ");
}
m=m/i;
}
else
{
break;
}
}
}
printf("\n\n");
return 0;
}

