#include <stdio.h>
int main()
{
int N, i = 2;    //2부터 시작

printf("숫자 입력 : ");
scanf("%d", &N);
printf("숫자 = %d\n%d =", N, N);

while(N != 1){    // 1이 아니면 끝날때까지 계속 진행
if(N % i == 0){  // i(2)의 배수면 진행
printf("%d", i);  //첫번째 i(2)를 출력
N = N / i;   // i(2)로 나누고 나머지(i(2)의 배수임)를 N에 대입
if(N != 1){   // 1이 아니면 계속 진행
printf("*"); //곱셈표시(*)를 출력
}
}
else if(!(N % i == 0)){  // i(2)의 배수가 아니면
i ++;		// i를 3으로 올려서 진행
}
else{   // 소수이면
printf("%d\n",N);  //자신을 출력
break;
}
}
printf("\n");

return 0;
}

