void bubblesort(char *v[], int n)
{
int i;
for(n=n-1;n >=0; n -=1)
{
for(i=0; i<n; i++)
{
if(strcmp(v[i], v[i+1]) > 0)
swap(v+i, v+i+1);
}
}
}

void swap(char *to[], char *from[])
{
char *temp;
temp = *from;

*from = *to;
*to = temp;
}

