int Bubble_sort(int *arr,int num_elements){

int i,temp;
int count=0;

while(1){

for(i=0;i<num_elements;i++){
if(arr[i] > arr[i+1]){
if(arr[i+1] == 0) break;
temp=arr[i];
arr[i]=arr[i+1];
arr[i+1]=temp;
count ++;
}

}if(count == 0) break;

else count=0;
}
return 0;
}

