#include <stdio.h>

int bubble_sort(int *arr, int *index, int num_element)
{
int i, j;

for(i = 0; i < num_element; i++) {
for(j = 0; j < num_element-i; j++) {
if( arr[j] > arr[j+1]) {
swap(&arr[j]);
swap(&index[j]);
}
}
}

return 0;
}

int swap(int *pele)
{
int *temp;

*temp = *pele;
*pele = *(pele+1);
*(pele+1) = *pele;

return 0;
}


