printf("%d번째 학생의 수학 성적을 입력하세요 : ",i+1);
scanf("%d", &mat[i]);

printf("%d번째 학생의 영어 성적을 입력하세요 : ",i+1);
scanf("%d", &eng[i]);
}
printf("평균  합격여부\n");
average(kor,mat,eng,ave);
rank(ave);
pass(ave,pass_point);

for(i=0;i<5;i++)
{
printf("%d    ",ave[i]);
if(ave[i]>=pass(ave,pass_point))
{
printf("합격\n");
}
else
{
printf("불합격\n");
}
}

return 0;
}

int average(int *kor, int *mat, int *eng, int *ave)
{
int i;

for(i=0;i<5;i++)
{
ave[i]=(kor[i]+mat[i]+eng[i])/3;
}

return *ave;
}

int rank(int *ave)
{
int i,j;
int max;

for(i=0;i<5;i++)
{
for(j=i+1;j<5;j++)
{
if(ave[i]<ave[j])
{
max = ave[i];
ave[i] = ave[j];
ave[j] = max;
}
}
}

return *ave;
}

int pass(int *ave, int pass_point)
{
int sum=0;
int i;

for(i=0;i<5;i++)
{
sum+=ave[i];
}

pass_point=sum/5;
return pass_point;
}

