{
do
{
temp = num1 % num2;
num1 = num2;
num2 = temp;

if(num1 % num2 == 0)
{
*meas = num2;
}
}while(num1 % num2 != 0);
}
else
{
do
{
temp = num2 % num1;
num2 = num1;
num1 = temp;

if(num2 % num1 == 0)
{
*meas = num1;
}
}while(num2 % num1 != 0);
}

return *meas;
}

