#include<stdio.h>

int n_base(int num, int *pi, int *arr, int n);
int m_base(int num, int *pi, int *arr, int m);

int main()
{
int num;
int i=0;
int *pi;
int arr[100];
int n,m;

pi = &i;

printf("정수를 입력하세요. : ");
scanf("%d",&num);
printf("사용할 진법(2~9)을 입력하세요. : ");
