for(i=i-1;i>=0;i--)
{
printf("%d",arr[i]);
}
printf("\n");

i = 0;

printf("사용할 진법(2~9)을 입력하세요. : ");
scanf("%d", &m);

m_base(num,pi,arr,m);

for(i=i-1;i>=0;i--)
{
printf("%d",arr[i]);
}

printf("\n");

return 0;
}

int n_base(int num, int *pi, int *arr, int n)
{
if(num>=n)
{
for(num;num>=1;num/=n)
{
arr[*pi] = num % n;
*pi += 1;
}
}
else
{
arr[*pi] = num;
*pi += 1;
}

return 0;
}

int m_base(int num, int *pi, int *arr, int m)
{
if(num>=m)
{
for(num;num>=1;num/=m)
{
arr[*pi] = num % m;
*pi += 1;
}
}
else
{
arr[*pi] = num;
*pi = 1;
}

return 0;
}

