// 문제 5
#include <stdio.h>
#include <stdlib.h>
#include <math.h>

int main()
{
int input, temp, n, m, i, digit = 0, conversion = 0, *arr;

// 몇 진법인지 확인
printf("몇 진법으로 입력하시겠습니까? ");
scanf_s("%d", &n);

// n진법으로 입력 받는 척하지만 16진수를 받는다 16진수라서 0 ~ F까지 입력 가능
printf("%d진법 수를 입력하세요 : ", n);
scanf_s("%x", &input);

//2진법부터 15진법까지는 연산이 필요하다
if (2<=n&&n<15)
{
// 16진수로 받아도 10진수로 저장하기 때문에 16진수로 몇 자리수인지 구함
temp = input;
while (temp >= 1)
{
temp /= 16;
digit++;
}

// 구한 자릿수만큼 배열을 생성
arr = (char*)malloc(sizeof(char)*digit);
// 한 자리씩 뜯어서 배열에 저장
for (i = 0; i < digit; i++)
{
arr[i] = (input % 16);
input /= 16;
}

// 한 자리씩 뜯은 수를 n진법에 맞게 복원하고 10진법으로 conversion에 저장
for (i = 0; i < digit; i++)
{
conversion += arr[i] * (int)powf((float)n, (float)i);
}
}
// 16진법으로 입력 받으면 편하다
else if (n==16)
{
conversion = input;
}

// 10진법을 m진법으로
printf("몇 진법으로 변환하겠습니까? ");
scanf_s("%d", &m);

// 10진법은 출력하기 쉽다
if (m == 10)
{
printf("%d(10)\n", conversion);
}
// 나머지는 연산이 필요하다
else
{
// m진법에서 필요한 자릿수 계산
digit = 0;
temp = conversion;
while (temp >= 1)
{
temp /= m;
digit++;
}

// m진법에서 필요한 자릿수 만큼 배열 재생성
arr = (char*)malloc(sizeof(char)*digit);
// m진법에 맞게 한 자리씩 뜯어서 배열에 문자로 저장
for (i = 0; i < digit; i++)
{
arr[i] = (conversion % m) + '0';
// 11~37진법을 위해서 알파벳 처리
if (arr[i] > '9')
{
arr[i] = arr[i] - 10 - '0' + 'A';
}
conversion /= m;
}

// 배열에 저장한 문자들을 역순으로 출력
for (i = digit - 1; i >= 0; i--)
{
printf("%c", arr[i]);
}
printf("(%d)\n", m);
}

return 0;
}

