int ave_score(int row, int (*score)[3], int *pstudent)
{
int i, j;
int sum = 0;
int total_ave = 0, total_sum = 0;
int ave_score[5];

for (i = 0; i < row; i++)
{
for (j = 0; j < 3; j++)
{
sum += score[i][j];
}
ave_score[i] = sum / 3;
total_sum += ave_score[i]; //평균 점수 전체 합 구하기
sum = 0;
}

total_ave = total_sum / row; //전체 평균 구하기
printf("전체 평균 점수 : %d\n", total_ave);

/*평균 높은 순 정렬*/

int max = ave_score[0]; // max 초기값 설정
int stu_max = pstudent[0]; // 학생 max 초기값 설정
int ordered_ave[5]; // 순서 재정렬 위한 배열
int ordered_stu[5];

for (i = 0; i < 5; i++)
{
for (j = 0; j < 5; j++)
{
if (max < ave_score[j])
{
max = ave_score[j];
stu_max = pstudent[j];
}
}
for (j = 0; j < 5; j++)
{
if (ave_score[j] == max)
{
ave_score[j] = 0;
pstudent[j] = 0;
}
}
ordered_ave[i] = max;
ordered_stu[i] = stu_max;
max = 0;
if (ordered_ave[i] < total_ave)
{
printf("%d등 : %d번째 학생 --> 평균 : %d --> 불합격\n", i + 1, ordered_stu[i], ordered_ave[i]);
}
else if (ordered_ave[i]>=total_ave)
{
printf("%d등 : %d번째 학생 --> 평균 : %d --> 합격\n", i + 1, ordered_stu[i], ordered_ave[i]);
}
}

printf("\n");
return 0;
