int Euclidean_algo(int a, int b)
{
int i = 2;
int r[100]; //나머지

if (a > b)
{
while (1)
{
r[0] = a%b;
if (r[0] == 0)
{
printf("최대공약수 : %d\n", b);
break;
}
r[1] = b%r[0];
if (r[1] == 0)
{
printf("최대공약수 : %d\n", r[0]);
break;
}
r[i] = r[i - 2] % r[i - 1];
if (r[i] == 0)
{
printf("최대공약수 : %d\n", r[i - 1]);
break;
}

i++;
}
}
else if (a < b)
{
while (1)
{
r[0] = b%a;
if (r[0] == 0)
{
printf("최대공약수 : %d\n", a);
break;
}
r[1] = a%r[0];
if (r[1] == 0)
{
printf("최대공약수 : %d\n", r[0]);
break;
}
r[i] = r[i - 2] & r[i - 1];
if (r[i] == 0)
{
printf("최대공약수 : %d", r[i - 1]);
break;
}
}
}
return 0;
}

