int length_str(char *str)
{
int i, length;
i = 0;
length = 0;
for (i; i < 50; i++)
{
if (str[i] == 0)
{
length = i;
break;
}
}
return length;
}

int num_checker(char *str, int length)
{
int i, j;
i = 0;
for (i; i < length; i++)
{
if (48 <= str[i] && str[i] <= 57)
{
j = 1;
}
else
{
j = 0;
break;
}
}
return j;
}

int str_to_int(char *str)
{
int num, i;
num = 0;
for (i = 0; i < length_str(str); i++)
{
num += (str[i] - '0') * pow(10, (length_str(str) - i - 1));
}
return num;
}

int euclidean_algo(int a, int b)
{
int r;
if (b > a)
{
int i;
i = a;
a = b;
b = i;
}
r = a % b;
if (a % b == 0)
{
r = b;
}
else
{
while (a % b != 0)
{
r = a % b;
a = b;
b = r;
}
r = b;

}
return r;
}

