#include <stdio.h>
#define LETTER 1 //charClass 구분
#define DIGIT 2
#define OTHER 3
#define ID 1 //token 구분
#define NUM 2
#define UNKNOWN 3
char stm[20] = "\0"; //입력 받은 문자열 저장
char lexeme[20] = "\0"; //addChar()에서 nextToken저장
char nextToken; //getChar()에서 받을 다음 글자
char *stmPointer = stm;
char *lexemePointer = lexeme;
int charClass; //글자 타입 구분
int printNum; // token 구분해 출력
int lex(); //프로토타입
char getChar();
void addChar();



main(){

printf("어휘 분석할 식 입력: ");
scanf("%s", &stm);

while(*stmPointer != '\0'){ //입력 받은 문자열이 끌날 때까지 어휘 분석 반복
printNum = lex();

switch(printNum){ //token 구분해서 출력
case ID:
printf("ID(%s)\t", lexeme);
break;

case NUM:
printf("NUM(%s)\t", lexeme);
break;

default:
printf("UNKNOWN(%s)\t", lexeme);
}
}
}



int lex(){;
lexemePointer = lexeme; //어휘 항목 구분시마다 초기화, lexeme=&lexeme[0]
nextToken = getChar(); //다음 글자 읽어 nextToken에 저장

switch(charClass){
case LETTER: //charClass가 LETTE로 시작하면 변수
addChar(); //lexeme 배열에 저장
nextToken = getChar(); //다음 글자 읽음
while(charClass == LETTER || charClass == DIGIT){ // 변수는 영문자 뒤에 숫자도 올 수 있음
addChar();
nextToken = getChar();
}
return ID;//지금까지 처리한 건 변수
break;

case DIGIT: //charClass가 DIGIT으로 시작하면 상수
addChar();
nextToken = getChar();
while(charClass == DIGIT){ //상수는 숫자 뒤에 숫자만 올 수 있음
addChar();
nextToken = getChar();
}
return NUM; //지금까지 처리한 건 상수
break;

default: //그 외엔 모두 UNKOWN 처리
addChar();
nextToken = getChar();
while(charClass == OTHER){ //++나 += 같은 연산자가 올 경우를 대비
addChar();
nextToken = getChar();
}
return UNKNOWN; //지금까지 처리한 건 그 외의 경우
}
}



char getChar(){ //다음 글자를 읽고, charClass결정
if(65 <= *stmPointer <= 90 || 97 <= *stmPointer <= 122){
charClass = LETTER; //포인터가 갖는 주소의 값이 'A'~'Z'이거나 'a'~'z'이면 LETTER로 설정
return *stmPointer;
}
else if(48<= *stmPointer <=57){
charClass = DIGIT; //포인터가 갖는 주소의 값이 '0'~'9'이면 DIGIT으로 설정
return *stmPointer;
}
else{
charClass = OTHER; //포인터가 갖는 주소 값이 그 외이면 OTHER로 설정
return *stmPointer;
}
stmPointer++; //다음 글자를 가리키도록 설정
}



void addChar(){ //lexeme에 현재 글자 저장
*lexemePointer = nextToken;
*(lexemePointer+1) = '\0'; //다음 칸에 바로 문자열 끝 표시
lexemePointer++; //포인터가 그 다음칸 가리키도록 설정
}

