/* 성적을 내림차순으로 정리하는 프로그램 */
#include<stdio.h>
int main()
{
int i, j, k, a, b, sum, dim[10], dim2[10], arr[10]={0};
sum = 0 ;

for(j=0;j<10;j++)
{
printf("%d번째 학생의 점수 : ",j+1);
scanf("%d",&dim[j]);
sum += dim[j] ;
}

printf("\n\n학생들의 총점은 %d이고, 평균은%d입니다.\n\n", sum, sum / 10);

while(arr[9]==0)				// 내림차순용 배열 마지막에 성적 입력될때까지 반복.
{
for(i=0;i<10;i++)				// dim[i]를 검사.
{
k = 0 ;				// 등수 k 초기화.

for(j=0;j<10;j++)
{
if(dim[i]<dim[j])		// dim[j]는 비교대상.
{
k+=1;			// dim[i]보다 잘한 학생수. 즉 k+1이 dim[i]의 등수.
continue;
}
}
arr[i] = k + 1;					// i번째 학생의 등수.
dim2[i] = i;					// i등인 학생의 번호.
}
}
for(a=1;a<=10;a++)				// a등인 학생을 찾아내는 반복문
{
for(b=0;b<10;b++) 			// 비교를 통해 검사
{
if(arr[b]>=a && arr[b]<a+1)
{
printf("%d등 학생은 %d번이며, 점수는%d점 입니다.\n", a, dim2[b]+1, dim[b]);
}
}
}

return 0;
}

