void sort(int arr[], int max){

int i, j, temp;
for(i=0;i<max-1;i++){
for(j=i;j<max;j++){
if(arr[i] < arr[j]){
temp = arr[i];
arr[i] = arr[j];
arr[j] = temp;
}
}
}

return;
}


