#include <stdio.h>

// 입력받은 수를 큰수부터 정렬
// Bubble - sort algorithm 이용

int main(void)
{
int i, j, p, q;
int score[5];

for (i = 0; i < 5; i++)
{
printf("%d 번째 학생의 성적은? \n", i + 1);
scanf("%d", &score[i]);
}

// 배열의 i와 i + 1 비교
// 작은 수를 왼쪽에 큰수를 오른쪽에 둔다.
// 위 작업을 배열의 길이 - 1 번 만큼 실행한다.
// 한 번 실행마다 가장 오른쪽 값이 정해진다.

// j의 값은 4로시작
// 배열 길이 - 1 만큼 루핑해야 하기 때문
for (j = 4; j > 0; j--)
{
for(i = 0; i < j; i++)
{
p = score[i];
q = score[i + 1];
if(p < q)
{
score[i] = q;
score[i + 1] = p;
}
}
}

for (i = 0; i < 5; i++)
{
printf("%d", score[i]);
}
printf("\n");

return 0;

}

