int borrow(int *borrowed,int i)
{
unsigned int a;//음수를 사용하지 못하게 함
printf("\n\n대출을 원하시는 도서의 번호를 입력해 주세요.\n(잘 모르신다면 목록이나 검색을 통해 알아 오세요)\n");
scanf("%d",&a);
if(a>i)//책이 있나 없나 확인 만약 없으면 다시 실행
{
system("cls");
printf("   다시 입력해 주세요.\n");
borrow(borrowed,i);
}
if(borrowed[a-1]==0)//이미 빌렸나 확인
{
system("cls");
printf("\n이책은 이미 대출이 되어있습니다.\n다음번에 다시 이용해 주세요.\n\n");
return 0;
}
borrowed[a-1]=0;
return 1;
}
int borrow_(int *borrowed,int i)
{
unsigned int a;//음수를 사용하지 못하게 함
printf("\n\n반납을 원하시는 도서의 번호를 입력해 주세요.\n(잘 모르신다면 목록이나 검색을 통해 알아 오세요)\n");
