}
int search(char (*a)[50],char *tem,int *arr,int i)
{
int e=0,d=0,b,c;//d=검색된 책의 권수;b=책의 번호,c=비교하는거,i=도서의 개수
for(b=0;b<i;b++)//책의 개수만큼 루프를 돌려 모든책을 비교하게함
{
for(c=0;tem[c]!=0;c++)
{
if (a[b][c]!=tem[c]) //순서대로 하나씩 비교
{
e++;	//틀리면 e에 1을 추가하여 밑에 있는 if문에서 걸러낸다.
break;
}
}
if(e)  //e가 0인것만 값을 저장한다.
{
e=0;
}
else
{
arr[d++]=b; //arr에 현재 책의 값을 넣는다.
}
}
return d;
}
