#include <stdio.h>

char* reverse_String (char *init_arr, char *result_arr);

int main(void)
{
char init_arr[100], result_arr[100];

printf("길이가 최대 100인 문자열 입력 : ");
scanf("%s", init_arr);

reverse_String ( init_arr, result_arr);

printf("문자열 역순 출력 : %s", result_arr);

return 0;
}

char* reverse_String (char *init_arr, char *result_arr)
{
int count = 0;

while(*init_arr) {
init_arr++;
count++;
}

for(i = 0; i < count; i++)
*result_arr[i] = *init_arr[count - i];


result_arr[count] = '\0';

return result_arr;
}

