int changeCharSize (char *init_arr, char *result_arr)
{
int i;

while(*init_arr) {
i = *init_arr;
if( i >= 65 && i <=90)
*result_arr = *init_arr + 32;
if( i >= 97 && i <= 122)
*result_arr = *init_arr - 32;
init_arr++;
result_arr++;
}
*result_arr = '\0';

return 1;
}

