#include<stdio.h>

int fun(char *str);

int main() {
char str[100];

printf("문자열을 입력하세요 : ");
scanf("%s", str);

fun(str);

printf("문자열이 뒤집혔습니다 : %s\n\n\n", str);
}

int fun(char *str) {
int i = 0;
char temp;

while (str[i]) {
i++;
}

for (int j = i-1; j > 0; j--) {
for (int k = 0; k < j; k++) {
temp = str[k];
str[k] = str[k + 1];
str[k + 1] = temp;
}
}

return 0;
}

