class Date
{
int year, month, day;
const int days[13]={0,31,28,31,30,31,30,31,31,30,31,30,31};
public:
void SetDate(int _year, int _month, int _day)
{
year=_year;
month=_month;
day=_day;
}
void AddDay(int inc)
{
day+=inc;
month+=day/days[month];
month%=12;
day%=days[month];
year+=month/12;
}
void AddMonth(int inc)
{
month+=inc;
month%=12;
year+=month/12;
}
void AddYear(int inc)
{
year+=inc;
}
void GetDate(void)
{
cout << year << "년 " << month << "월 " << day << "일" <<endl;
}
};

#include <iostream>
using namespace std;

class Date
{
int year, month, day;
int days[13] = {0,31,28,31,30,31,30,31,31,30,31,30,31};

public:
void set_date(int _year, int _month, int _day){
year = _year;
month = _month;
day = _day;
}
void add_day(int inc){
day += inc;
while(1){
if(year % 4 == 0) days[2] = 29;
else days[2] = 28;

if(day > days[month]){
day -= days[month];
month++;
if(month == 13){
year++;
month = 1;
}
}
else break;
}
}
void add_month(int inc){
month += inc;
year += month / 12;
month %= 12;
}
void add_year(int inc){
year += inc;
}
void get_date(){
cout << year << "/" << month << "/" << day << endl;
}
};

int main(){
Date date;
date.set_date(2016,2,1);
date.add_day(400);
date.get_date();
return 0;
}

