int calc_plus(struct BigNum *bignum, struct BigNum *bignum2, struct BigNum *bignum3)
{

int i=0, j=0;
int common_size = 0, intermediate_result = 0;
int additional = 0;
struct BigNum initialize_struct;

printf("bignum의 주소값 : %x [1] : %x, [2] : %x \n\n", bignum, bignum2, bignum3);

bignum3->sign = bignum->sign;

if (bignum->d_total_digit >= bignum2->d_total_digit)
{
common_size = bignum->d_total_digit - bignum2->d_total_digit;

// 소수 부분은 --> 방향으로 계속 간다
// 따라서, 공통된 부분은 함께 계산해주고
// 나머지 뒤의 것은 따로 계산

i = A_equal_B_plus_C_by_repeat_size(bignum->d_total_digit - common_size, &bignum3->d_digit[0], &bignum->d_digit[0], &bignum2->d_digit[0]);

/* This Function is C to in B repeat untill A */
/* bignum -> d_total_digit ' - i ' <-- cuase of  */
/* This case just need to repeat except for common part */
Copy_B_To_A_by_repeat_size(bignum->d_total_digit - i, &bignum3->d_digit[i], &bignum->d_digit[i]);


/* counting up if value of array is upper than 10 */
additional = counting_up(&bignum->d_total_digit, &bignum3->d_digit);
bignum3->d_total_digit = bignum->d_total_digit;

}
else
{
common_size = bignum2->d_total_digit - bignum->d_total_digit;
// 소수 부분은 --> 방향으로 계속 간다
// 따라서, 공통된 부분은 함께 계산해주고
// 나머지 뒤의 것은 따로 계산

i = A_equal_B_plus_C_by_repeat_size(bignum2->d_total_digit - common_size, &bignum3->d_digit[0], &bignum2->d_digit[0], &bignum->d_digit[0]);

Copy_B_To_A_by_repeat_size(bignum2->d_total_digit - i, &bignum3->d_digit[i], &bignum2->d_digit[i]);

additional = counting_up(&bignum2->d_total_digit, &bignum3->d_digit);

bignum3->d_total_digit = bignum2->d_total_digit;


}

if (bignum->i_total_digit >= bignum2->i_total_digit)
{
common_size = bignum->i_total_digit - bignum2->i_total_digit;

// 정수 부분은 <-- 방향으로 추가된다 ( Ex : 1000 , 10 )
// 그래서 공통된 부분만큼 bignum 에서 따로 계산해주고
// 나머지 뒤의 값들은 함께 계산 해준다

// return last value of for loop
//  == >  i = common_size;
i = Copy_B_To_A_by_repeat_size(common_size, &bignum3->i_digit[0], &bignum->i_digit[0]);

j = A_equal_B_plus_C_by_repeat_size(bignum->i_total_digit - common_size, &bignum3->i_digit[i], &bignum->i_digit[i], &bignum2->i_digit[0]);


bignum3->i_digit[i + j - 1] += additional;
additional = 0;


/* counting up if value of array is upper than 10 */

counting_up(&bignum->i_total_digit, &bignum3->i_digit);

bignum3->i_total_digit = bignum->i_total_digit;

}
else
{
common_size = bignum2->i_total_digit - bignum->i_total_digit;
i = Copy_B_To_A_by_repeat_size(common_size, &bignum3->i_digit[0], &bignum2->i_digit[0]);
j = A_equal_B_plus_C_by_repeat_size(bignum2->i_total_digit - common_size, &bignum3->i_digit[i], &bignum2->i_digit[i], &bignum->i_digit[0]);

bignum3->i_digit[i + j - 1] += additional;
additional = 0;

counting_up(&bignum2->i_total_digit, &bignum3->i_digit);

bignum3->i_total_digit = bignum2->i_total_digit;

}

printf(" \n --- 덧셈 결과 출력 --- \n");
print_number(bignum3);
printf(" ----------------------- \n");

return 0;
}


