#include<stdio.h>
#include<stdlib.h>
#include<string.h>

#pragma warning(disable:4996)

struct data{ // 저장해줄 구조체를 선언
char str[1000];
};

int main(){
int i, j;
int n;
data *jax;
data imsi;

printf("how many words?\n"); // 사용자로부터 몇개의 단어를 입력받을지 출력
scanf("%d", &n); // n에 입력받은 단어의 수를 저장
jax = (data *)malloc(sizeof(data)*n); // 구조체 포인터를 이용해서 n크기의 메모리를 할당해줌 ( malloc사용 stdlib.h)
for (i = 0; i < n; i++){
printf("[%d] ", i);
scanf("%s", (jax + i)->str);
}
printf("BEFORE sorting\n"); // 소팅전 단어들을 출력

for (i = 0; i < n; i++){
printf("[%d] %s\n", i,(jax + i)->str);
}

for (i = 0; i < n; i++){ // 버블정렬을 이용해서 정렬
for (j = 0; j < n - i - 1; j++){
if (strcmp((jax + j)->str, (jax + j + 1)->str)>0){
imsi = *(jax + j);
*(jax + j) = *(jax + j + 1);
*(jax + j + 1) = imsi;
}
}
}
printf("After sorting\n"); // 소팅후 단어들을 출력

for (i = 0; i < n; i++){
printf("[%d] %s\n", i,(jax + i)->str);
}
return 0;
}

